function day1($input) {
  $res = 0;
  for ($i = 0; $i < strlen($input)-3; $i++) {
    $found = FALSE;
    for ($j = $i; $j < $i+3; $j++) {
      for ($k = $j+1; $k < $i+4; $k++) {
        if ($input[$j] == $input[$k]) {
          $found = TRUE;
          break;
        }
      }
    }
    if (!$found) {
      $res = $i+4;
      break;
    }
  }

  return $res;
}

function day2($input) {
  $res = 0;
  for ($i = 0; $i < strlen($input)-13; $i++) {
    $found = FALSE;
    for ($j = $i; $j < $i+13; $j++) {
      for ($k = $j+1; $k < $i+14; $k++) {
        if ($input[$j] == $input[$k]) {
          $found = TRUE;
          break;
        }
      }
    }
    if (!$found) {
      $res = $i+14;
      break;
    }
  }

  return $res;
}

